	xy.sort(function(a, b){return a[4]- b[4];});//一旦成長率でソート
	xy.unshift(["キャラ名","レアリティ","職業","覚醒上限","成長率","AS1","AS2","PS1","PS2","PS3","専属武器","BS1","BS2","BS3"]);
